import { Component } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { TemplateRef, ViewChild } from '@angular/core';

@Component({
  selector: 'app-add-edit',
  templateUrl: './add-edit.component.html',
  styleUrls: ['./add-edit.component.css']
})
export class AddEditComponent {
  name: any;
  email: any;
  @ViewChild('popupFormTemplate') popupFormTemplate!: TemplateRef<any>;
  constructor(public dialog: MatDialog){}
  openDialog(): void {
    const dialogRef = this.dialog.open(this.popupFormTemplate, {
      width: '400px',
    });

    dialogRef.afterClosed().subscribe(result => {
      // Handle dialog close if needed
    });
  }
  addedit(addedit: any, arg1: { width: string; }) {
    throw new Error('Method not implemented.');
  }

  closeDialog(): void {
    this.dialog.closeAll();
  }

  submitForm(): void {
    // Handle form submission here
    // You can access the form values via this.name and this.email
    this.closeDialog();
  }
}
